<script lang="ts" setup>
import galxeRewardData from '~/content/rewards/galxe'
import { useUserInfo } from '@/stores/userInfo'
import type { Reward } from '~/types/rewards'

defineProps({
  locked: {
    type: Boolean,
    default: true,
    required: false,
  },
})

defineEmits(['openGalxeModal'])

const store = useUserInfo()

const cardColor = '#A55AE7'

const showModal: Ref<boolean> = ref(false)

function openModal() {
  showModal.value = true
  document.documentElement.style.overflow = 'hidden'
}

function closeModal() {
  showModal.value = false
  document.documentElement.style.overflow = 'auto'
}

const activatedMultipliers = computed(() => {
  return []
})

const activateCard = computed(() => {
  return store.loggedIn && store.user.galxeLinked
})

// TODO: understand the logic for this component
const data = {
  pool: {
    name: 'Overnice',
    icon: '',
  },
  userNIMStakedWithPool: 20000,
}

const x = ref(0)

onMounted(() => {
  setTimeout(() => {
    // Get user leaderboard % position from Galxe api
    x.value = 79
  }, 500)
})
const mr = computed(() => {
  return (x.value / 100) * 49
})
</script>

